import styled  from "@emotion/styled";

export const Section = styled.section`
padding-top: 30px;
    max-width: 500px;
    margin-right: auto;
    margin-left: auto;
    border-radius: 10px;
    font-size: 16px;
    line-height: 1.19;
    letter-spacing: .03em;
    background-color: white;
    margin-bottom: 15px;
    text-align: center;
`

export const Title = styled.h2`  font-size: 24px;
     font-weight: 700;
    font-size: 24px;
  margin-bottom: 30px;
  `
export const SectionList = styled.ul`  display: flex;
  list-style: none;`
export const Item = styled.li` display: flex;
  flex-direction: column;`
export const Label = styled.span` width: 100px;
  font-size: 16px;
  font-weight: bold;`
export const Percentage = styled.span`  font-size: 16px;
  color: #4c4c4c;
  font-weight: bold;`
