//   location={user.location}
//   avatar={user.avatar}
//   stats={user.stats}
// />;

// ReactDOM.render(Profile, document.querySelector('root'));

ReactDOM.createRoot(document.getElementById('root')).render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);
