// Компонент повинен приймати один проп friends – масив об'єктів друзів.

// Компонент повинен створювати DOM наступної структури.
const FriendList = friends => {
  return;
  <ul class="friend-list">
    {friends.map(friend => (
      <FriendListItem
        avatar={friend.avatar}
        name={friend.name}
      ></FriendListItem>
    ))}
  </ul>;
};

