import styled  from "@emotion/styled";

export const Item = styled.li`
  display: flex;
  align-items: center;
  margin-bottom: 10px;
  border: 1px solid #ccc;
  padding: 10px;
  :last-child {
  margin-bottom: 0;
`
export const Span = styled.span`
 display: inline-block; width: 10px;
height: 10px;
 border-radius: 50%;
  margin-right: 10px;
`

